  /*
  Recursive Function to generate Sample Employee Records - 5 Columns Employee ID, Name, Department, Salary, Department Id
   */
  def generateSampleEmployeeData(sampleNumberRows: Int): ArrayBuffer[EmployeeData] = {
    if (sampleNumberRows == 0) {
      println(sequenceEmployees)
      sequenceEmployees
    } else {
      val eid = initialEmployeeId-sampleNumberRows
      // val employeeNameFromArray = nameList(Random.nextInt(1000))
      val firstName = randomNameGenerator(Random.nextInt(5)+5)
      val lastName = randomNameGenerator(Random.nextInt(5)+4)
      val salary = getSalary
      val departmentId = Random.nextInt(departmentMap.size) + 1
      val department = departmentMap.get(departmentId).getOrElse("Default")

      val employee = EmployeeData(eid, firstName+" "+lastName, department, salary, departmentId)
      sequenceEmployees.+=:(employee)
      generateSampleEmployeeData(sampleNumberRows - 1)
    }

  }

  def getSalary: Int = {
    val baseSalary = 20000
    val variableSalaryPercentage = Random.nextInt(50)
    val finalSalary = baseSalary + (baseSalary*(variableSalaryPercentage/100f))
    finalSalary.toInt
  }

  def randomNameGenerator(length: Int): String = {
    val chars = ('a' to 'z') ++ ('A' to 'Z')
    randomStringFromCharacters(length, chars)
  }

  def randomStringFromCharacters(length: Int, chars: Seq[Char]): String = {
    val sb = new StringBuilder
    for (i <- 1 to length) {
      val randomNum = util.Random.nextInt(chars.length)
      sb.append(chars(randomNum))
    }
    sb.toString
  }

